INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('637gr', 'Ask for a specific person by name when entering the office. ', 'ordered-list-item', 1);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('5o3i5', 'Always present your name card to the receptionist. ', 'ordered-list-item', 1);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('il58', 'Always say, “May I please see Mr. X?” rather than “Is Mr.X in? ', 'ordered-list-item', 1);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('bhuj5', 'If the secretary indicate Mr.X is unavailable, leave your card and ask that it is to be given to the party.', 'ordered-list-item', 1);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('acdom', 'If the waiting is long, ask the receptionist if it might be more convenient to see Mr.X at another time. ', 'ordered-list-item', 1);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('9rlai', 'When shown into the office of the prospect, shake hand, make eye contact, smile genuinely, and announce your name and company clearly. ', 'ordered-list-item', 1);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('11gvc', 'Give the impression that the only purpose for your being in the area is to see Mr.X. ', 'ordered-list-item', 1);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('5hsqv', 'Be brief. ', 'ordered-list-item', 1);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('fvgk1', 'At the conclusion of the call, end by saying something like, “I certainly took more of your time than I had promised to take. Thank you so much. Mr.X.” ', 'ordered-list-item', 1);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('637gr', 'Preparation Prior to Sales Call', 'ordered-list-item', 2);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('786st', ' Greeting and Introduction', 'ordered-list-item', 2);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('djs8j', 'Qualifying', 'ordered-list-item', 2);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('gr41', 'Surveying', 'ordered-list-item', 2);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('e8ln', 'Handling Objections', 'ordered-list-item', 2);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('4ktpq', 'Presentation', 'ordered-list-item', 2);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('o36e', 'Closing', 'ordered-list-item', 2);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('52k4t', 'Customer Maintenance', 'ordered-list-item', 2);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('637gr', 'People easily become friendly and feel happy if you call him by his name. So, first ensure how should you address him and then call him by his name. ', 'ordered-list-item', 3);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('farl8', 'While talking for quite a long time shows your caller that you are eagerly listening to him. So sometimes making noises like “hmm”, “yes”, “ok”, “I understand” can make the conversation spontaneous. ', 'ordered-list-item', 3);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('atpbv', 'Otherwise in middle of a long discussing if you keep silent for the whole time your caller may be confused whether you are listing or whether you are still on line or not. So, don’t make your caller confused.', 'ordered-list-item', 3);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('4vg4l', 'Receiving call demands deep attention. If you talking over telephone and at the same time doing other business then you cannot concentrate on anything.', 'ordered-list-item', 3);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('3k7q8', 'Make your tone as friendly as possible. Try to smile while talking, the way you talk reflects in your tone.', 'ordered-list-item', 3);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('15oqo', 'Don’t neglect any call. Take it as a business opportunity for your organization.', 'ordered-list-item', 3);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('e705', 'If you or the caller cannot listen to other then offer him to call back.', 'ordered-list-item', 3);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('637gr', 'Sometimes by hearing the tone you may not identify whether you caller is male or female. It is better to ask “How should I address you?” or “May I have your name please?”', 'ordered-list-item', 4);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('en1sf', 'If you need to transfer any call then say “Please allow me to transfer your call to Mr. X. Could you please hold down for a minute?” After that if the caller allows you and says Yes or Ok then transfer the call.', 'ordered-list-item', 4);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('9utq5', 'If you found the extension is not reachable or dead then say “Thanks for holding. But I am afraid Mr. Y is not available. Would you like to leave a message for him or call back later?”', 'ordered-list-item', 4);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('2div2', 'If you found the extension is busy then say “Thanks for holding. Mr. Y is busy and still on line. Would you like to leave a message for him or call back later?”', 'ordered-list-item', 4);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('4vake', 'While finishing a conversation say “Thank you Mr. X for calling. Have a nice day.”', 'ordered-list-item', 4);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('985cj', 'Some common phrases are: “May I have your name please”, “May I have your contact number please”, “Mr. X please let me repeat the message…..Is that all right?”, “Mr. X could you please hold down for a minute?”', 'ordered-list-item', 4);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('637gr', 'Answer the telephone promptly within 3 rings.', 'ordered-list-item', 5);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('5l22t', 'Make the caller know your work area, your name and offer appropriate greeting. If it is an inbound call then just mention your department and if it is outbound call then mention name of the hotel with your identity.', 'ordered-list-item', 5);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('4o0tn', 'Always have pen and paper on hand, specially front desk personnel should always be ready to keep records.', 'ordered-list-item', 5);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('76ku9', 'Listen carefully. Pay close attention to details being expressed by the caller.', 'ordered-list-item', 5);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('3fje5', 'Make the caller feel that they have your undivided attention. Make an occasional acknowledgment of what he/she says. Mention the name of the caller, once established.', 'ordered-list-item', 5);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('9rqhj', 'If you have to ask the caller to hold on, explain why. Wait for the caller’s agreement before actually putting him/her on hold.', 'ordered-list-item', 5);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('6pp7b', 'If you have to transfer the call, explain why and make sure that the caller is properly introduced to the next party. If the purpose of the call has been said already, repeat the same to the next party.', 'ordered-list-item', 5);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('96obe', 'If you say you will call back, do so as soon as possible. Give him some sorts of idea that how long he or she need to wait.', 'ordered-list-item', 5);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('8lgu6', 'Sometimes you may not understand whether the caller is a man or lady. To be on the safe side politely ask his or her name or you can say \\\\\\\\how should i address you\\\\\\\\?', 'ordered-list-item', 5);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('cu6e3', 'Repeat back any details and follow up in writing (if necessary).', 'ordered-list-item', 5);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('5fcjm', 'Close conversation politely. Always say “thank you for calling.”', 'ordered-list-item', 5);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('4iq13', 'Let’s caller hang up first.', 'ordered-list-item', 5);
INSERT INTO IS4103.SALESCALLGUIDELINEWYSIWYGENTITY (KEYSTRING, TEXT, "TYPE", SALESCALLGUIDELINE_SALESCALLGUIDELINEID) 
	VALUES ('7juhn', 'Try to satisfy your guest with proper information. If you don''t know detail then transfer the call to the right person. Never give wrong information.', 'ordered-list-item', 5);
